import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import './reset.css';
import "./App.css"
import { Pagination } from 'swiper/modules';

import Page1 from "./10_Mainpage/sub/Page1"
import Page2 from "./10_Mainpage/sub/Page2"
import Interest from "./20_Interest/Interset"
import Roadmap from './30_Roadmap/Roadmap';
import 'animate.css';

export default function App() {
  const [activeIndex, setActiveIndex] = useState(0);

  const handleSlideChange = (swiper) => {
    setActiveIndex(swiper.activeIndex);
    console.log(swiper.activeIndex)
    if (swiper.activeIndex === 1) {
      console.log('切换到了第 3 页，执行特定操作');
    }
    if (swiper.activeIndex === 2) {



    }
    if (swiper.activeIndex === 3) {
      console.log('切换到了第 3 页，执行特定操作');
    }
  };

  return (
    <>
      <Swiper
        pagination={{
          dynamicBullets: true,
          clickable: true,

        }}
        onSlideChange={handleSlideChange}
        modules={[Pagination]}
        className="mySwiper"

      >
        <SwiperSlide className="swiperSlide"><Page1 /></SwiperSlide>
        <SwiperSlide className="swiperSlide"><Page2 /></SwiperSlide>
        <SwiperSlide><Interest /></SwiperSlide>
        <SwiperSlide><Roadmap /></SwiperSlide>

      </Swiper>
    </>
  );
}
