import React, { useEffect, useRef, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import './reset.css';
import "./App.css"
// import required modules
import { Pagination } from 'swiper/modules';

import Mainpage from './10_Mainpage/Mainpage';
import Mbti from './20_Mbti/Mbti';
import Interest from './30_Interest/sub/Interest';
import Roadmap from './40_Roadmap/Roadmap';

export default function App() {
  const swiper = useRef(null)

  useEffect(() => {
    const swiperInstance = swiper.current.swiper;
    console.log(swiperInstance)

    swiperInstance.on('paginationRender', () => {
      // 当pagination渲染时执行的回调函数
      const activeIndex = swiperInstance.activeIndex;

      if (activeIndex === 1) {
        // 滚动到底部
        window.scrollTo(0, document.body.scrollHeight);
      }
    });
  }, [])



  return (
    <>
      <Swiper
        pagination={{
          dynamicBullets: true,
          clickable: true,

        }}
        modules={[Pagination]}
        className="mySwiper"
        ref={swiper}
      >
        <SwiperSlide className="swiperSlide"><Mainpage /></SwiperSlide>
        <SwiperSlide><Mbti /></SwiperSlide>
        <SwiperSlide><Interest /></SwiperSlide>
        <SwiperSlide><Roadmap /></SwiperSlide>
      </Swiper>
    </>
  );
}
