import React from 'react'
import styles from "./ball.module.css"

const Ball = (props) => {
  const { number, color, top, left, move = "moveUpDown" } = props;
  return (
    <div className={`${styles.ball} ${move}`} style={{
      width: number, height: number, backgroundColor: color, top: top, left: left, animation: `${move} 1s ease-in-out infinite alternate`
    }}>
    </div>
  );
}

export default Ball;
